#define DRAW_OUTER_CIRCLE
const float ring_radius = 0.85;
const int iterations = 5;
const float glow_intensity = 0.0134;
const float circle_thickness = 0.005;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uvo = uv;

    //inside ball
    vec3 final_col = vec3(0.0);
    for ( float i = 0.;i<float(iterations);i++) {
        uv = fract(uv*1.5) - 0.5;
        float d = length(uv);
        vec3 col = vec3(1.0,3.0,2.0) * (ring_radius-length(uvo));
        d = sin(d*8. + iTime*1.)/8.;
        d = abs(d);
        d = pow(glow_intensity / d, 1.);
        col *= d*.4;
        final_col += col;
    }
    final_col = clamp(final_col,0.0,1.0);
    fragColor = vec4(final_col,1.0);

    //outer ring (comment out define at top to not draw outer circle)
    #ifdef DRAW_OUTER_CIRCLE
    vec3 col_oc = vec3(0.,1.0,.8);
    float dd = length(uvo);
    dd -= ring_radius;
    dd = abs(dd);
    dd = smoothstep(0.0,circle_thickness,dd);
    dd = 1.-dd;

    fragColor = vec4(final_col + col_oc * dd,1.0);
    #endif
}
