// Was debugging a new shader I'm working on, and stumbled on this optical illusion.
// At least to me, the lines appear slanted, when they are actually straight.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec3 col = vec3(0.);
    uv*= 2.;
    uv = fract(uv) - .5;
    col += 2.*uv.x * 2.*uv.y;    
    fragColor = vec4( col, 1.0 );;
}
