void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord / iResolution.xy; 
    vec2 uvcSquares = fract(uv * 10.0) - 0.5; 
    vec3 col = 0.5 + 0.5 * cos(iTime + uv.xyx + vec3(0,3, 5));

    float dist = max(abs(uvcSquares.x), abs(uvcSquares.y));

    if(dist < 0.25)    
        fragColor = vec4(col*0.0, 0.0); //zeros out the color so that the squares appear
    else
        fragColor = vec4(col, 0.5); 
}
