const float TILE_SIZE = 60.;
const float BORDER_SIZE = 1.;

const float PERIOD = 4.;
const float AMPLITUDE = 0.5;

const float PI = atan(1.) * 4.;

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float row = fragCoord.y / TILE_SIZE;
    float offset = AMPLITUDE * (cos(floor(row) / PERIOD * 2. * PI) + 1.) / 2.;
    float column = fragCoord.x / TILE_SIZE + offset;

    float v = mod(floor(column), 2.);
    v = mix(v, 0.5, step(fract(row), BORDER_SIZE / TILE_SIZE));
    v = mix(v, 0.5, step(fract(column), BORDER_SIZE / TILE_SIZE));

    fragColor = vec4(vec3(v), 1.0);
}
