vec3 createPlanet( in vec2 uv, in float dist, in float size )
{
    float time = iTime;
    time = time/dist;
    vec2 planetLoc = vec2(cos(time), sin(time)/2.0)*dist;
    vec3 col = clamp((1.0-length(planetLoc/2.0-uv)*(50.0/size+sin(time)*10.0*dist))*vec3(1.0,1.0,1.0), 0.0, 1.0);
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy*2.0-1.0;

    vec3 col = vec3(0);

    vec3 sun = (1.0-length(uv)*50.0)*vec3(1.0,1.0,1.0);
    vec3 sunGlo = (1.0-length(uv)+abs(abs(uv.x)-abs(uv.y))*(15.0+10.0*sin(iTime*10.0)))*vec3(1.0,0.5,0.0);
    col += sun + sunGlo;
    col = clamp(col, 0.0, 1.0);
    col += createPlanet(uv, 1.0, 1.0);
    col += createPlanet(uv, 1.5, 1.5);
    col += createPlanet(uv, 0.5, 0.5);

    col += length(uv)*cos(iTime+uv.xyx+vec3(0,2,4));

    fragColor = vec4(col,1.0);
}
