int roundMod(float x, float invPow2) {
    return int(round(fract(x / invPow2)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 scaled = fragCoord / iResolution.xy * 0.5 / exp2(iTime + 1.0);
    int c = 0;
    int i = 1;
    float invPow2 = 1.0;
    float limit = iResolution.y * exp2(iTime + 1.0);
    while(invPow2 >= 1.0 / limit) {
        invPow2 *= 0.5;
        c += roundMod(scaled.x, invPow2) * roundMod(scaled.y, invPow2);
        ++i;
    }
    float color = float(c);
    fragColor = vec4(color, color, color, 1);
}
