void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    float alpha = abs(uv.x - 0.5) * 2.0;

    float edg = 0.5;

    alpha = clamp(alpha - (1.0 - edg), 0.0, 1.0);
    alpha /= edg;
    alpha = pow(alpha, 6.0);

    vec3 color = mix(texture(iChannel0, uv).rgb, vec3(1.0), alpha);

    // Output to screen
    fragColor = vec4(color,1.0);
}
