float hash11(float p)
{
    vec2 p2 = fract(vec2(p * 5.3983, p * 5.4427));
    p2 += dot(p2.yx, p2.xy + vec2(21.5351, 14.3137));
    return fract(p2.x * p2.y * 95.4337)*0.5+0.5;
}

void mainImage( out vec4 o, in vec2 i )
{
    float rain_speed = 2.5;
    float rain_streak = 3.0;
    vec2 uv = 1.+i/iResolution.xy;
    float c = 1.-fract((uv.y * 0.5 + iTime * rain_speed+0.1) * hash11(floor((uv.y*50.) + (uv.x*150.)))*0.5)*rain_streak;
    vec3 droplet = vec3(0.5, 0.7, 0.8)*1.5;
    o = vec4(mix(vec3(0, 0, 0), droplet, c*0.2), 1.);
}
