void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy*2.0-1.0;
    uv.x *= iResolution.x/iResolution.y;

    float t = cos(iTime+uv.x-uv.y+6.0)/2.0;

    vec2 uvb = uv;
    uvb.x /= 0.1;

    float wave = sin(uvb.x*uv.y*t+2.0*iTime)/8.0;
    float uvdi = length(uv);
    uvdi *= 0.5;
    uvdi = smoothstep(1.0,0.5,uvdi);

    // Time varying pixel color
    vec3 col = 0.5+0.5*cos(iTime+uv.x+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col*wave/(uvdi),1.0);
}
