void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Pi, the ratio of a circle's circumference to its diameter.
    const float M_PI = 3.14159265358979323846264338327950288;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float ratio = iResolution.x/iResolution.y;
    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 white  = vec3(1,1,1);

    vec2 center = vec2(0.5,0.5);
    float num = 100.;
    float speed = 2.;
    bool inside = false;

    for(float i = 0. ; i<2.*M_PI ; i+= 2.*M_PI/num){
        vec2 circle = vec2(center.x,center.y);
        float ang = i+(iTime+5000.)*speed;
        float offset = 0.25*sin(ang*i*uv.x);
        circle.x += offset;
        float rad = 0.03;

        vec2 tmp = vec2(circle.x-center.x,circle.y-center.y);

        circle.x = (tmp.x * cos(ang) - tmp.y * sin(ang))+center.x;
        circle.y = (tmp.x * sin(ang) + tmp.y * cos(ang))*ratio+center.y;

        float dist = sqrt(pow(circle.x-uv.x,2.)*ratio+pow(circle.y-uv.y,2.)/ratio);

        inside = inside || dist<rad;
    }

    // Output to screen
    fragColor = vec4(inside?col:white,1.0);
}
