#define I iResolution

mat2 rot(float a) {
  float b = sin(a), c = cos(a);
  return (mat2(c, b, -b, c));
}


float smax(float a, float b, float c) {
  float d = clamp(.5 + .5 * (b - a) / c, 0., 1.);
  return (mix(b, a, d) - c * d * (1. - d));
}

float smin(float a, float b, float c) { return (-smax(-a, -b, c)); }

float r(vec3 a) {
  float b = abs(a.y) - .3;
  b = abs(b);
  return (max(length(a.xz) - .5 * b, abs(a.y) - .3));
}

vec3 s(vec3 a) {
  for (float b = 0.; b < 3.; b++)
    a.xy = abs(a.xy), a.xy *= rot(3.141593 * b / 3.);
  return (a);
}

float rep(inout vec2 u, float rep) {

  float r = 6.28 / rep; // each radian

  float a = round(atan(u.y, u.x) / r) * r; // aligned uv

  u = vec2(dot(u, vec2(cos(a), sin(a))), dot(u, vec2(-sin(a), cos(a))));

  return floor(mod((round(a / r) + rep * .5), rep)); // index
}

float f(vec3 p) {

  float py = abs(p.y) - .3;
  py = abs(py);

  return max(length(p.xz) - .5 * py, abs(p.y) - .3);
}

float g(vec3 p) {

  p.xz *= rot( iTime );
  p.yz *= rot( iTime );


  rep(p.xz, 8.);

  float d = .1;

  rep(p.xy, 8.);
  p.x -= .0;
  p.xy *= rot(1.57);

  d = min(d, f(p));

  d = smin(d, -(length(p) - .15), .05);

  return d * .5556;
}

vec3 nrm(in vec3 b) {
  vec2 a = vec2(1, -1) * .5773;
  return (normalize(a.xyy * g(b + a.xyy * 5e-4) + a.yyx * g(b + a.yyx * 5e-4) +
                    a.yxy * g(b + a.yxy * 5e-4) + a.xxx * g(b + a.xxx * 5e-4)));
}



vec3 df(vec3 f) {
  vec3 i = vec3(0, 0, -1), c, a;
  float d, b, e;
  for (; e < 64.; e++)
    a = i + f * d, b = g(a), d += b;
  if (b < 1e-3) {
    float j = dot(nrm(a), vec3(.5, 1, -1));
    c = vec3(.2 * j) + reflect(nrm(a), normalize(a)) * .1;
  }
  return (c);
}

vec3 lrn( float ll ){
    return mix(
        vec3( 0.17, 0.98, 0.85 ),
        vec3( 0.16, 0.73, 0.97 ),
        ll
    );
}

void mainImage(out vec4 i, in vec2 j) {
  vec2 l = (j * 2. - I.xy) / I.y;
  vec3 b, a, y = vec3(0, 0, -.1), o = vec3(l * .5, 1);
  float p;
  b = lrn( l.y / 2. + .5 );

  float d = 1e+10, e = 0., c = 0.;

  for (float q = 0.; q < 64.; q++)
    a = y + o * p, 
    c = g(a), p += c, d = c < d ? c : d;
  if (c < 1e-3) {
    float f = dot(nrm(a), vec3(.5, 1, -1));
    b = vec3(-.1 * f) + reflect(nrm(a), normalize(a)) * reflect(nrm(a), normalize(a)) * pow(f, 2.);

    b += .5, b += df(reflect(nrm(a), o + abs(o) * o.y));
    b += lrn( a.z ) * .2;

    vec3 lp = vec3(.0, 0.2, -.9);
    lp.xy *= rot( iTime );
    lp.zy *= rot( iTime );


    float fp = dot( normalize( lp ), nrm(a) ) ;

    if( abs( fp ) < .9 ){
        b = mix(
            b,
            lrn( fp ),
            clamp( 1. - abs(fp), 0., .3 )
        );
    }

  }
  i = vec4(b, 1);
}
