// alternative of Hell Diving
// https://shadertoy.com/view/lcGGzK


float smin( float d1, float d2, float k )
{
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}

float gyroid (vec3 p) { return dot(cos(p),sin(p.yzx)); }

float fbm(vec3 p)
{
    float result = 100.;
    float a = .5;
    for (float i = 0.; i < 34.; ++i)
    {
        p.z += (result)*.6;
        p.z += iTime*.1;
        result = smin(result, abs(gyroid(p/a)*a), 1.5*a);
        a /= 1.6;
    }
    return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    vec3 ray = normalize(vec3(uv,.1));
    vec3 color = 0.5 + 1. * cos(vec3(0,2,4)*6. + 3.5 + fbm(ray) * 20.);

    fragColor = vec4(color,1.0);
}
