void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalize coordinates
    vec2 uv = fragCoord / iResolution.xy;

    // Calculate angle based on fragment position
    float angle = atan(uv.y - 0.5, uv.x - 0.5);

    // Calculate distance from center
    float dist = length(uv - 0.5);

    // Calculate segment index based on angle and distance
    float segmentIndex = floor((mod(angle + dist * 10.0, 2.0 * 3.14159) * 5.0) / (2.0 * 3.14159));

    // Calculate new position based on segment index
    vec2 newUV = vec2(0.0);
    if (segmentIndex == 0.0) {
        newUV = mod(uv + vec2(0.5, -0.5), 1.0);
    } else if (segmentIndex == 1.0) {
        newUV = mod(uv + vec2(-0.5, 0.5), 1.0);
    } else if (segmentIndex == 2.0) {
        newUV = mod(uv + vec2(-0.5, -0.5), 1.0);
    } else if (segmentIndex == 3.0) {
        newUV = mod(uv + vec2(0.5, 0.5), 1.0);
    } else {
        newUV = uv;
    }

    // Output final color
    fragColor = texture(iChannel0, newUV);
}
