void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float Size = 24.0;
    vec2 Pos = floor(fragCoord / Size);
    float PatternMask = mod(Pos.x + mod(Pos.y, 2.0), 2.0);
    fragColor = PatternMask * (vec4(1.0, 1.0, 1.0, 0.2) - (sin((iTime * 20.0 + Pos.x) / 24.0))) / 8.0;
}
