#define SMOOTHSTEP(a, b, x) smoothstep(a, b, x)
#define NOISE(x) fract(sin(dot(x, vec2(12.9898, 78.233))) * 43758.5453)

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    // Background
    vec3 bgColor = mix(vec3(0.1, 0.0, 0.2), vec3(0.0, 0.0, 0.05), uv.y * 0.5 + 0.5);
    vec3 color = bgColor;

    // Orbs
    for (int i = 0; i < 5; i++) {
        vec2 pos = vec2(sin(iTime*0.1 + float(i)), cos(iTime*0.1 + float(i)*1.5)) * 0.5;
        float dist = length(uv - pos);
        float radius = 0.2;
        float glow = 0.1;
        float orb = SMOOTHSTEP(radius, radius - glow, dist);

        // Color shifting
        float time = iTime + float(i) * 1.23;
        vec3 orbColor = vec3(NOISE(vec2(time * 0.1)), NOISE(vec2(time * 0.15)), NOISE(vec2(time * 0.2)));
        color += orbColor * orb;
    }

    fragColor = vec4(color,1.0);
}
