void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalize pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y; // Adjust for aspect ratio

    // Time variable
    float time = iTime;

    // Sample audio data from iChannel0
    float audio = texture(iChannel0, vec2(0.1, 0.0)).r; // Bass frequency
    float midAudio = texture(iChannel0, vec2(0.5, 0.0)).r; // Mid frequency
    float trebleAudio = texture(iChannel0, vec2(0.9, 0.0)).r; // Treble frequency
    float audioFlash = (audio + midAudio + trebleAudio) / 3.0;

    // Background color with pulsing effect
    vec3 bgColor = 0.1 + 0.1 * sin(time * 3.0 + uv.xyx) * vec3(0.2, 0.2, 0.3);

    // Head (circle)
    float head = length(uv) - 0.4;

    // Ears
    float leftEar = length(uv - vec2(-0.3, 0.4)) - 0.1;
    float rightEar = length(uv - vec2(0.3, 0.4)) - 0.1;

    // Eyes (animated blink effect)
    float blink = step(0.9, sin(time * 2.0));
    float leftEye = length(uv - vec2(-0.15, 0.0)) - 0.05 * blink;
    float rightEye = length(uv - vec2(0.15, 0.0)) - 0.05 * blink;

    // Daft Punk visor (rectangle with audio-driven glow)
    float visor = max(abs(uv.y + 0.1) - 0.05, abs(uv.x) - 0.25);
    vec3 visorColor = vec3(0.5, 0.0, 1.0) + vec3(1.0, 0.5, 0.0) * audioFlash;

    // Glowing outline effect
    float outline = smoothstep(0.01, 0.02, abs(head)) +
                    smoothstep(0.01, 0.02, abs(leftEar)) +
                    smoothstep(0.01, 0.02, abs(rightEar));

    // Initialize color
    vec3 color = bgColor;

    // Draw head
    if (head < 0.0) color = vec3(0.6, 0.5, 0.4);

    // Draw ears
    if (leftEar < 0.0 || rightEar < 0.0) color = vec3(0.6, 0.5, 0.4);

    // Draw eyes
    if (leftEye < 0.0 || rightEye < 0.0) color = vec3(0.0, 0.0, 0.0);

    // Draw visor with audio-driven glow
    if (visor < 0.0) color = visorColor;

    // Add glowing outline
    color += vec3(0.2, 0.2, 0.2) * outline;

    // Output to screen
    fragColor = vec4(color, 1.0);
}
