vec4 cubic_overlay(vec4 col1, vec4 col2) {
    return (1.-col2)*(col1*col2) + (col1)*(1.-(1.-col1)*(1.-col2));
}

vec4 regular_overlay(vec4 col1, vec4 col2) {
    return round(col1)*(1.-2.*(1.-col1)*(1.-col2)) + 2.*round(1.-col1)*col1*col2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 col1 = texture(iChannel0, uv);
    vec4 col2 = texture(iChannel1, uv);

    fragColor = round(uv.x+sin(iTime)/2.)*cubic_overlay(col1,col2) + (1.-round(uv.x+sin(iTime)/2.))*regular_overlay(col1,col2);

    fragColor += round(pow((1.-abs(uv.x+sin(iTime)/2.-0.5)),30.0)/1.6);
}
