void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float angle=0.02;
    mat2 rot=mat2(cos(angle), -sin(angle),sin(angle), cos(angle));    
    uv-=0.5;uv*=rot;uv+=0.5;
    float s=mod(-iTime*0.5,2.0);
    float g=1.0-length(uv-0.5)*0.75;
    g*=1.0-sin(iTime*0.1)*0.9;
    vec2 offset1=texture(iChannel1,(uv-0.5)* g*(1.0+mod(s,1.0))).rg;
    vec2 offset2=texture(iChannel1,(uv-0.5)*-g*(1.0+mod(s+0.5,1.0))).rg;
    vec2 offset=mix(offset1,offset2,abs(0.5-mod(s,1.0))*2.0)-0.5;
    float col0=texture(iChannel0,uv+offset*0.1).x;
    float col1=texture(iChannel0,uv+offset*0.14).y;
    float col2=texture(iChannel0,uv+offset*0.18).z;
    vec3 col=vec3(col0,col1,col2)*1.01;

    col+=vec3(pow(1.0-length(uv-0.5+vec2(sin(iTime)*0.1,cos(iTime*1.6831)*0.1)),132.0*(0.5+offset1.x*0.5)));
    col/=1.0+length(col)*0.03;
    col-=0.001;
    col*=min(iTime,1.0);

    fragColor=vec4(clamp(col,vec3(0.0),vec3(1)),1);
}
