void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalize pixel coordinates to range [0.0, 1.0]
    vec2 p = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    // Zooming effect - use the time variable to zoom out
    float zoom = 1.0 + 0.5 * sin(iTime * 0.2); // Zooming out effect

    // Mandelbrot Set parameters
    vec2 c = p / zoom - vec2(0.5, 0.5);  // Shift the fractal center
    vec2 z = vec2(0.0);
    int maxIterations = 256;
    float iter = 0.0;

    // Mandelbrot iteration
    for(int i = 0; i < maxIterations; i++)
    {
        if(dot(z, z) > 4.0)
            break;
        z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) + c;
        iter++;
    }

    // Color based on number of iterations
    float color = float(iter) / float(maxIterations);

    // Output to the screen (using smoothstep for gradient effects)
    fragColor = vec4(vec3(color), 1.0);
}
