#define PI 3.1415926
#define N(x) vec2(sin(x),cos(x)) 
void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    uv *= 1.25;
    vec3 col =vec3(0.0);
    float angle = (2.0/3.0)*PI;
    float screenAngle = (5.0/6.0)*PI;
    uv.x = abs(uv.x);
    vec2 screeN = N(screenAngle);
    uv.y += tan(screenAngle)*0.5;
    uv -= screeN*max(0.0,dot(screeN,uv-vec2(0.5,0.0)))*2.0; 
   // col += smoothstep(0.01,0.0,abs(dot(screeN,uv-vec2(0.5,0.0))));
    //迭代图形
    float scale = 1.0;
    uv.x +=0.5;
    vec2 n = N(angle);
    for(int i=0;i<6;i++){
        uv *=3.0;
        scale *= 3.0;
        uv.x -= 1.5;
        uv.x =abs(uv.x) -0.5;
        uv -= n*min(0.0,dot(n,uv))*2.0;
    }
    float d = length(uv - vec2(clamp(uv.x,-1.0,1.0),0.0));
    col += smoothstep(1.0/iResolution.y,0.0,d/scale);
   // col.rg += uv;
    fragColor = vec4(col,1.0);
}
