// Displays a maze as produced by the simple but amazing Commodore 64 BASIC Programm
// 10 PRINT CHR$(205.5+RND(1)); : GOTO 10
// See also http://10print.org/
//
// Version 1.0 (2013-12-31)
// Simon Stelling-de San Antonio
//
// Many thanks to Inigo Quilez (iq) for Shadertoy.
//
// V1.0  2013-12-31  Initial public version

#define TILESIZE 16.0
#define WALLTHICKNESS 1.0
#define SPEED 0.08

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy;
	p.y -= (iTime * iResolution.y * SPEED);
	p = floor(p);
	vec2 s = floor(p/TILESIZE);
	float d = sin(514.32*(sin(s.x*494.65+s.y*132.55)+cos(s.x*243.43+s.y*553.65)));
	p = mod(p,TILESIZE);
	if (d >= 0.0) {	d = (p.x-p.y);
	} else {		d = (p.x+p.y-TILESIZE);
	}
	if (abs(d) < TILESIZE*WALLTHICKNESS/8.0 + 0.5) {	d = 1.0;
	} else {	d = 0.0;
	}
	// Commodore 64: light blue: RGB 134,122,222; dark blue:  RGB 72, 58, 170
	fragColor = vec4(62.0*d+72.0, 64.0*d+58.0, 52.0*d+170.0, 256.0) / 256.0;
}
