// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

vec3 lig = normalize(vec3(0.9,0.35,-0.2));

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec3 nor = vec3( 0.0, 1.0, 0.0 );
	float dif = max(dot(nor,lig),0.0);

	vec2 position=(fragCoord.xy/iResolution.xy) * 10.1;
	vec3 pos = vec3( position.x, 0.0, position.y );

	// lights
	vec3 brdf = vec3(0.0);
	float cc  = 0.55*texture( iChannel0, 1.8*0.02*pos.xz + 0.007*iTime*vec2( 1.0, 0.0) ).x;
	cc += 0.25*texture( iChannel0, 1.8*0.04*pos.xz + 0.011*iTime*vec2( 0.0, 1.0) ).x;
	cc += 0.10*texture( iChannel0, 1.8*0.08*pos.xz + 0.014*iTime*vec2(-1.0,-1.0) ).x;
	cc = 0.6*(1.0-smoothstep( 0.0, 0.025, abs(cc-0.4))) + 
		0.4*(1.0-smoothstep( 0.0, 0.150, abs(cc-0.4)));
	dif *= 1.0 + 2.0*cc;

	brdf += 3.5*dif*vec3(1.00,1.00,1.00);

	vec3 col = vec3( 0.0, 0.6, 1.0 ) * brdf;	
	col *= 0.65;

    fragColor = vec4( col, 1.0 );
}
