// Fork of https://www.shadertoy.com/view/XsffWS
// A simple disk with background and foreground color =)

#define DISK_RADIUS .5
#define BACKGROUND vec3(.06)
#define FOREGROUND vec3(.0, .5, 1.)

void mainImage(out vec4 fragColor, in vec2 fragCoord){
  vec2 uv = 2. * (fragCoord + -.5*iResolution.xy) / iResolution.y;  // Courtesy of Fabrice!

  float uv_radius = length(uv);

  float disk_radius = DISK_RADIUS + .2*sin(2.*iTime);
  float disk = 1. - smoothstep(disk_radius, disk_radius+.01, uv_radius);

  fragColor.rgb = mix(BACKGROUND, FOREGROUND, vec3(disk));
}
