void mainImage(out vec4 f,vec2 c)
{
    f-=f;
	vec2 u=c/iResolution.y*-4.+10.,t=vec2(3,1)*.0003*iTime+9.;
    float w=.04;
    for(int i=0;i<20;++i)
    {
        u=u*sin(t.x)+cos(t.y)*vec2(-u.y,u.x);
        u*=1.2+f.xy*.06;
        w*=.96;
    	f+=w*(abs(sin(u.x))+abs(cos(u.y)));
    }
    f.x=sin(f.x*4.);
    f*=f;
    f*=f;
}
