float rand(float n){return fract(sin(n) * 43758.5453123);}

float noise(float x)
{
    float result;
    float i = floor(x);
	float f = fract(x); 

    // linear
    result = mix(rand(i), rand(i + 1.0),f);

    // Hermite
    //result = mix(rand(i), rand(i + 1.0),smoothstep(0.,1.,f));

    if( iMouse.z>0.0 )
    {
    	// Cubic Curve
 		result = mix(rand(i), rand(i + 1.0),f*f*(3.0-2.0*f));
    }
    return result;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

	float r = noise(uv.x*30.0);

	fragColor = vec4(r,r,r,1.0);


}
