void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	float s = sin(iTime);
	float c = cos(iTime);
	vec2 metaballs[3];

	metaballs[0] = vec2(-c * 100.0 + -100.0, s * 5.0 + -250.0);
	metaballs[1] = vec2(c * 101.0, s * 5.25 + 86.0);
	metaballs[2] = vec2(-c * 110.0, s * 11.5+328.0);

	vec2 q = (fragCoord.xy * 2.0 - iResolution.xy) * 2.0;

	fragColor =
		vec4(0.3, 0.3, 0.5, 1.0) * clamp(150000.0 / pow(distance(q.xy, metaballs[0]), 2.0), 0.0, 1.2) +
		vec4(0.3, 0.3, 0.5, 1.0) * clamp( 80000.0 / pow(distance(q.xy, metaballs[1]), 2.0), 0.0, 1.2) +
		vec4(0.3, 0.3, 0.5, 1.0) * clamp(151000.0 / pow(distance(q.xy, metaballs[2]), 2.0), 0.0, 1.2);
}
