#define time iTime
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(0.);
    for (int i = 0; i < 70; i++) {
        vec2 flake = vec2(cos(time*float(i)/6.)/7., abs(sin(time+float(i))));
        uv.x -= (float(i))/1000.;
        col.b +=  1.0 - smoothstep(.03, .031, length(uv - flake));
        col.b += (1.0 - smoothstep(0.03, .08, length(uv - flake))) / 8.;
        col.r += (1.0 - smoothstep(0., .031, length(uv - flake))) / 1.5;
    }

	fragColor = vec4(col,1.0);
}
