// exponential smooth min (k = 32);
float smin( float a, float b, float k )
{
    float res = exp( -k*a ) + exp( -k*b );
    return -log( res )/k;
}

float sphere(vec3 pos, float radius)
{
    return length(pos) - radius;
}

float ss(float x, float a){
	return a*(0.5+0.5*sin(x));
}

float ss(float x, float a,float p){
	return a*pow(0.5+0.5*sin(x),p);
}

float distfunc(vec3 pos)
{
	vec3 npos = pos;
    float s1 = sphere(npos, 1.5
					  +ss(pos.y*3.0+iTime*10.0,0.05)
					  +ss(pos.x*3.0+iTime*10.0,0.1)
					 );

	// movement amplitude for all blobs
	// same formula as camera shake (should make them global I guess)
	float amp = ss(iTime*3.0,2.0);


	// Note that the x coords are all pushed towards the cam (by pow'ing the sin on x)
	// No point twiddling blobs where we can't see them
	npos = pos-amp*vec3(pow(sin(iTime*1.7),2.0),cos(iTime*3.0),sin(iTime));
	float s2 = sphere(npos, 0.2+ss(iTime*0.45,0.6));

	npos = pos-amp*vec3(pow(sin(iTime*-1.3),2.0),cos(iTime*0.3)*0.7,sin(iTime*1.1));
	float s3 = sphere(npos, 0.2+ss(iTime*1.945,0.6));

	npos = pos-amp*vec3(pow(sin(iTime*0.3),2.0),cos(iTime*-1.9),sin(iTime*0.7));
	float s4 = sphere(npos, 0.2+ss(iTime*2.3,0.6));

	float smoother = 8.0;
	//mix the blobby
	return smin(smin(smin(s1,s2,smoother),s3,smoother),s4,smoother);
}

vec3 render(vec2 screenPos,float screen){
	vec3 upDirection = vec3(0.0, 1.0, 0.0);  // global up

	vec3 cameraOrigin = vec3(8.0, 0.0, 0.0); // where cam is
	vec3 cameraTarget = vec3(0.0, 0.0, 0.0); // where it's pointing
	vec3 cameraDir = normalize(cameraTarget - cameraOrigin); // camera is pointing this way
	vec3 cameraRight = normalize(cross(upDirection, cameraOrigin)); // right is htis way
	vec3 cameraUp = cross(cameraDir, cameraRight); // up is that way

    // LOOK AT ME MAKE THIS MAD 3D!
	cameraOrigin += cameraRight*screen*0.05;

	// hey look over there
	vec3 rayDir = normalize(cameraRight * screenPos.x + cameraUp * screenPos.y + cameraDir);


	const int MAX_ITER = 200; // max jumps
	const float MAX_DIST = 20.0; // ain't nothing out here, give up
	const float EPSILON = 0.001; // basically there

	float totalDist = 0.0;
	vec3 pos = cameraOrigin;
	float dist = EPSILON;

	for (int i = 0; i < MAX_ITER; i++)
	{
		// Either we've hit the object or hit nothing at all, either way we should break out of the loop
		if (dist < EPSILON || totalDist > MAX_DIST)
			continue; // If you use windows and the shader isn't working properly, change this to continue;

		dist = distfunc(pos); // Evalulate the distance at the current point
		totalDist += dist;
		pos += dist * rayDir; // Advance the point forwards in the ray direction by the distance
	}

	vec3 c = texture(iChannel0,-rayDir).rgb;;

	if (dist < EPSILON)
	{
		vec2 eps = vec2(0.0, EPSILON);
		vec3 normal = normalize(vec3(
			distfunc(pos + eps.yxx) - distfunc(pos - eps.yxx),
			distfunc(pos + eps.xyx) - distfunc(pos - eps.xyx),
			distfunc(pos + eps.xxy) - distfunc(pos - eps.xxy)));

		vec3 lightDir = normalize(vec3(2.0,-1.0,-.5));

		float diffuse = max(0.0, dot(lightDir, normal));
		float specular = 0.1+pow(diffuse, 32.0);
		float edging = pow(length(normal*vec3(0.0,1.0,1.0)),4.0); // I really should redo this to move with the camera...
		;
		c =
			0.5*specular
			+0.7*diffuse*vec3(0.1,1.0,0.1)
			+0.7*edging*vec3(0.5,1.0,0.5)*diffuse
			;
		c = mix(c,texture(iChannel0,reflect(-rayDir,normal)).rgb,c.r); // mix by r because it mostly comes from the specular
	}
	return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float screen = sign(uv.x-.5);
    uv.x = mod(uv.x,0.5)*2.;
    uv = (uv-.5);
    uv.y /= (iResolution.x/2.)/iResolution.y;
	vec2 uvmod = vec2(0.03-ss(iTime*3.0+3.14159,0.03,0.3),0.0);

	vec3 c = vec3(render(uv,screen));


	fragColor = vec4(c,1.0);

}
