float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
	f = f*f*(3.0-2.0*f);

	vec2 uv = (p.xy+vec2(37.0,17.0)*p.z) + f.xy;
	vec2 rg = textureLod( iChannel0, (uv+ 0.5)/256.0, 0.0 ).yx;
	return mix( rg.x, rg.y, f.z );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p = uv * 2.2 - 1.0;

	vec2 rotationXY = vec2(0.6 * cos(iTime), 0.6 * sin(iTime));
    // resize width so that it's not an ellipse but a circle
    p.x *= iResolution.x / iResolution.y;
    //p.xy += rotationXY;

    float brightness = 0.8;
    float sphereScale = 0.7;
    float radius = sqrt(dot(p, p));
    vec3 diff = vec3(0.1, 0.1, 0.2);
    vec3 spec = vec3(1.0);
    vec2 lightPos = vec2(0.5, -0.5) * vec2(cos(iTime * 0.5) * -0.95, sin(iTime * 0.5));

    vec3 color = diff * 0.3;
    vec3 bg = color;

    if (radius < sphereScale) {
    	color = diff + smoothstep(1.0, -0.3, noise(200.0 * mix(vec3(uv, 1.0), diff, 0.1 + 0.5 *sin(mod(iTime * 0.4 , 0.5)) * 0.5 + 0.5)));

    	float f = 1.0 - smoothstep(-0.2, 0.6, length(p - lightPos));
        color += f * spec * brightness;

        f = smoothstep(sphereScale * 0.98, sphereScale, radius);
        color = mix(color, bg, f);
    }

    fragColor = vec4(color, 1.0);
}
