const float sinB = 3.14159 * 2.;
const float deg = 180. / 3.14159;

const vec4 K = vec4(1., 2. / 3., 1. / 3., 3.);
vec3 hsv2rgb(vec3 c)
{
	// from http://lolengine.net/blog/2013/07/27/rgb-to-hsv-in-glsl
    vec3 p = abs(fract(c.xxx + K.xyz) * 6. - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0., 1.), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.x;
	vec2 target = iMouse.xy / iResolution.x;
    float t = iTime * 10.;
	vec2 p = uv - target;

    vec4 m_sample = texture(iChannel0, uv);
    float d = distance(uv,target);
    float x = (d * d * sinB * (sin(t) * .2 + 2.)) * 10.; 
    float v = sin(x - t) * 0.5 + 0.5;

    float r = 360. / (sin(t ) * 180. + 360.);

    float a = acos(p.x / d) * deg;
    float l = sin(a / r);

    fragColor = vec4(hsv2rgb(vec3(1.-v,1.-l,v)), 1.);
}
