#define time iTime
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p = uv / 3.;
    p.x *= iResolution.x / iResolution.y;
    p.y -= .2;
    vec3 blue = vec3(.4, .5, 1.0);


    vec3 col = vec3(1.);

    float old = p.x * 200.;
    p.x *= 200.;
    p.x = float(int(p.x));

    float y = cos(p.x/10.+time)/10.;

    if (p.y  <= y) {
        col = blue;
        col = vec3(1.0-smoothstep(0., .4, abs(p.x - old)));
    }

    col.b = 1.0;
    col.g = .7;


    col *= smoothstep(-.4, .12, p.y);
	fragColor = vec4(col,1.0);
}
