// (x,y) -> (r,g,b)
// Note this will only work while L < Yr and Yg but > Yb.
const float L = 0.15;

const float Yr = 0.3;
const float Yg = 0.59;
const float Yb = 0.11;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 plane = fragCoord.xy / iResolution.xy;

    // Map y directly to b
    float LSubB = L - 0.11 * plane.y;

    // Use b value to determine r and g
    float xAt0 = LSubB / Yr;
    float xAt1 = LSubB / Yg;

    // r = xAt0 when x=0, g = xAt1 when x=1, interpolate in between.
    float r = (1.0 - plane.x) * xAt0;
    float g = plane.x * xAt1;

    fragColor = vec4(r,g,plane.y,1);
}
