void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 position = (fragCoord.xy/iResolution.xy) - 0.5 ;
    float time = iTime;

    //1. Izveido mainiigo, kas mainaas attieciibaa pret dziesmas konkreetaa parastaa ekvalaizera liinijas daljas
    float beat = texture( iChannel1, vec2(0.0, 0.01 ) ).y;

    //2. Izveido taa kaa cilpu, pa y asi, platumu un kustiibu ietekmee 1. komentaara mainiigais (beat)
	float y = beat * position.x * sin(10000.0 * position.x + 100. * time * 0.01);
	y = 3. / (700. * abs(position.y - y));

    //3. Izveido taa kaa cilpu, pa x asi, platumu un kustiibu ietekmee 1. komentaara mainiigais (beat)
    float x = beat * position.y * sin(1000.0 * position.y + 59. * time *0.01);
	x = 3. / (1700. * abs(position.x - x));

    //4. Izveido viduu sauli, kuras lielums ir atkariigs no 1. komentaara mainiigaa (beat)
    float saule = 5./length(40. / beat*length(position - vec2(0, 0)));

    //5. Nodroshina 2., 3. un 4. komentaara izveidoto mainiigo kraasu mainju un nodroshina to, ka saule atrodas taa kaa zem 2. un 3. komentaara mainiigajiem
    //
	vec4 vstari_top = vec4(position.y*2. - y,y, y*10., y);
    vec4 vstari_bottom = vec4(position.y*(-1.5) - y,y, y, y);
    vec4 vstari_right = vec4(position.x*1. - x,x, x*10., x);
    vec4 vstari_left = vec4(position.x*(-0.5) - x,x, x, x);
    //

    //6. Nodroshina izveidoto mainiigo un objektu realizaaciju
    fragColor = vec4(y+x+saule+vstari_top+vstari_bottom+vstari_right+vstari_left);
}
