// Created by Stephane Cuillerdier - @Aiekick/2016
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

float n;

float df(vec2 p, float z)
{
    //https://www.shadertoy.com/view/4lVGWV
    float a = atan(p.x,p.y);
    float b = 6.2831/n;
    return cos(floor(.5+a/b)*b-a)*length(p) * cos(z*1.5);
}

void mainImage( out vec4 f, vec2 g )
{
    n = 3. + floor(7. * (sin(iTime * .5)*.5+.5));

    if (iMouse.z > 0.)
        n = floor(10.*iMouse.x/iResolution.x);

    vec2 p = g /= 80., a; f-=f-9.;

    for(int x=-3;x<=3;x++)
    for(int y=-3;y<=3;y++)
        p = vec2(x,y),
        a = sin( iDate.w + 9. * fract(sin((floor(g)+p)*mat2(2,5,5,2)))),
        p += .5 + .5 * a - fract(g),
        f = min(f, df(p,a.x));

    f = sqrt(vec4(10,5,2,1)*f); // 249c => i prefer original color and i prefer color with 10 instead of 9 :)
    //f *= vec4(8,5,3,1); // 241c => similar for code size
}
