void mainImage( out vec4 r, in vec2 fd )
{
	vec2 u = fd.xy*4./ iResolution.xy -2.;
    u.x *= 16./9.;
    float a = sin(iTime), s = sin(a), c = cos(a), d=0.;
    vec3 e = vec3(-s,0.,-c), o = vec3(u, 1.), p;
    for (a = 0.; a< 3.; a++){p=e*d+o;d+=length(p*d) - 1.;}
   	a = d*length(u);
    u.x = c*a,
    u.y = s*a;
    r = mix(texture(iChannel0, u), vec4(0.9), d);
}
