void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 tZ;
    vec2 Z = (fragCoord.xy - iResolution.xy/2.0)/( iResolution.x/11.0 );

    vec2 C = vec2(sin(iTime/2.0), cos(iTime/2.0));
    vec4 color;

    int iterations = 0;
    int max_iterations = 12;

    float rate;
    float threshold_squared = 100.0;

    while ( (iterations < max_iterations) && (dot(Z, Z) < threshold_squared) )
    {
        tZ.x = Z.x * Z.x - Z.y * Z.y + C.x;
        tZ.y = Z.x * Z.y * 1.0 + C.y;
        Z = tZ;
        iterations++;
    }

    rate =  float(iterations)/float(max_iterations);
    if ( rate > 0.9 )
    {
        //color = vec4(0.1, 0.1, 0.1, 1.0);
        color = vec4( cos(Z.x), sin(Z.y), 0.5, 1.0) * 0.8;
    }
    else
    {
        color = vec4( rate, exp(Z.x), rate, 1.0);
        //color = vec4( sin(rate), cos(1.0-rate), exp(rate), 1.0);
    }

	fragColor = color;
}
