renderpass.code:
//sampler2D iChannel0;
int boxes = 100;
float spinSpd = 1.0;
float boxSize = 10.0;
float noiseStr = 0.04;
float space = 0.1;
vec3 col1 = vec3(0.2,0.4,1.0);//fade from
vec3 col2 = vec3(0.00,0.025,0.25);//fade to
vec3 col3 = vec3(0.1,1.0,0.8);
vec3 col4 = vec3(0.001,0.05,0.05);

vec3 box(vec2 pos, vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xx - pos;
    uv *= mat2(cos(-iTime*spinSpd), -sin(-iTime*spinSpd),
               sin(-iTime*spinSpd), cos(-iTime*spinSpd));
    uv *= boxSize;

    vec3 col = vec3(0.0);
    col += mix(col1, col2, uv.x)/vec3(2.0);
    col += mix(col3, col4, uv.y)/vec3(2.0);

    if(length(uv) > 1.0) {
        uv = vec2(0.0);
        col = vec3(0.0);
    }

    return col;//vec3(uv, 0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 res = vec2(1.0, iResolution.y/iResolution.x);

    vec2 n = texture(iChannel0, fragCoord.xy / iResolution.xx*64.0).xy*2.0-1.0;
    n*= vec2(noiseStr);

    vec2 pos = vec2(0.5);
	vec3 outCol = vec3(0.0);

    for(int i = 0; i < boxes; i++) {
    	outCol += box (pos*res + n, fragCoord);
        pos += vec2(cos(iTime)*space, sin(iTime)*space);
    }
	fragColor = vec4(outCol,1.0);
}
