void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord / iResolution.xy;

    if (uv.y > 0.5) { // if top half of screen
        uv.y -= 0.5;
        fragColor = texture(iChannel0, uv);
    }

    else { // if bottom half of screen

        uv.y = 0.5 - uv.y;

        vec2 noiseStretch = vec2(1000.0, 100.0);
        float distanceRiverTraveled = iTime * 0.3;

        vec2 noisePosition = fragCoord / noiseStretch;
        noisePosition.x += distanceRiverTraveled;

    	float noise = texture(iChannel1, noisePosition).r;

    	float xMove = noise * 0.15 - 0.075;

    	vec2 imagePosition = vec2(
    		uv.x + xMove,
        	uv.y
    	);
    	vec4 image = texture(iChannel0, imagePosition);

        float waterBrightness = 0.65;
		fragColor = image * waterBrightness;

    }
}
