vec2 uv = vec2(0);
vec2 rotateCCW (vec2 pos, float angle){ 
  float ca = cos(angle),  sa = sin(angle);
  return pos * mat2(ca, sa, -sa, ca);  
}

float squircle(vec2 pos, float radius, float power){
  vec2 p = abs(pos - uv) / radius;
  float d = (pow(p.x,power) + pow(p.y, power) - pow(radius, power)) -1.0;
  return 1.0 - clamp (46.0*d, 0.0, 1.0);
}

float fu(vec2 pos, float size){
  uv = rotateCCW(uv,0.5);
  uv.y*=0.9;
  float a = squircle(vec2(pos.x-.4,pos.y-.9),size*1.1, 1.6);   
  uv.x*=1.1;
  float b = squircle(vec2(pos.x-0.3,pos.y+.6),size*0.9, 1.6);
  return a*b;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 ratio = vec2(iResolution.x / iResolution.y, 1.0); 
  uv = (2.0 * fragCoord.xy / iResolution.xy - 1.0) * ratio;
  uv.x-=0.2;
  vec4 tex = texture(iChannel0,fragCoord.xy / iResolution.xy);
  float e = fu(vec2(0),1.0);
  float f = fu(vec2(0.8,-0.6),0.7);
  float g = fu(vec2(-0.1,1.5),0.62);
  fragColor = mix(vec4(1.0),tex,clamp(e+f+g,0.0,1.0));
}
