#define N(a) normalize(a)
void mainImage(out vec4 c,in vec2 x)
{
	vec2 o=vec2(0,3),r=iResolution.xy;
	vec3 D=N(vec3(x-r/2.,-r.y)),O=o.xxy,l=o.yyy,t=o.xxx;
	float a=dot(D,O-t);
	vec3 p=O+D*(-sqrt(a*a-dot(O-t,O-t)+1.)-a),n=N(p-t),lv=N(l-p);
	c=vec4(0.5*(dot(lv,n)+pow(max(0.,dot(N(O-p),reflect(-lv,n))),9.)));
}
