// t âˆˆ [0, 1]
vec4 flag(float t) {
    if (t <= 1./6.) {
        return vec4(0.89, 0.01, 0.01, 1.);
    } else if (t <= 2./6.) {
        return vec4(1., 0.55, 0., 1.);
    } else if (t <= 3./6.) {
        return vec4(1., 0.93, 0., 1.);
    } else if (t <= 4./6.) {
        return vec4(0., 0.5, 0.15, 1.);
    } else if (t <= 5./6.) {
        return vec4(0., 0.3, 1., 1.);
    } else {
        return vec4(0.46, 0.03, 0.53, 1.);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    fragColor = flag(1.-uv.y);
}
