void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float gradient = (0.7-float(fragCoord.y / iResolution.x))*1.4;
    float cellSize = 12.0;
    vec2 currentOffset = vec2(0.0,0.0);
    float currentTime = 0.0;
    float d = 0.0;
    for(float moblur = 0.0;moblur<0.2;moblur += 0.03){
        currentTime = iTime + moblur;
        for(int xpos = -2;xpos<=2;xpos++){
            for(int ypos = -2;ypos<=2;ypos++){
                currentOffset = vec2(xpos,ypos);
                vec2 uv = (fragCoord.xy / iResolution.x);
                vec2 uvStep = (ceil((uv)*cellSize-vec2(0.5,0.5)+currentOffset)/cellSize);
                float y = fract(sin(dot(uvStep.xy,vec2(8616.1566,26968.489)))*968423.156)-0.5;
                float x = fract(sin(dot(uvStep.xy,vec2(1242.9898,42678.233)))*33758.5453)-0.5;

                float rm1 = sin(currentTime*2.5)*2.5/cellSize;
                float rm2 = cos(currentTime*2.5)*2.5/cellSize;

                vec2 newPosition = vec2(uvStep.x + x*rm1, uvStep.y + y*rm2);

                d += 0.2*(distance(newPosition.xy,uv.xy)*cellSize<0.4&&distance(newPosition.xy,uv.xy)*cellSize>0.3?1.0:0.0);
            }
        }
    }

    fragColor = vec4(d);
}
