void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scale = 30.0;


	vec2 p = (fragCoord.xy - vec2(0.5)) / scale;
    vec2 uv = floor(p) * scale / iResolution.xy;
    vec2 rel = fract(p);

    float t = iTime;
    float thickness = pow(uv.x + sin(uv.y * 5.0 + t) * 0.2 + 0.2, 1.5) * 0.3;

    float q = iTime * 0.15;
    vec2 offset = vec2(sin(q), cos(q)) * 0.1;
	fragColor = texture(iChannel0, uv + offset) * clamp((thickness - max(rel.x, rel.y)) * scale, 0.0, 1.0);
}
