float smoothv = 0.001;
float maxCircles = 10.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (sin(iTime) * maxCircles) * vec2(fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;

    vec2  v = vec2(floor(uv.x + 0.5), 0.0);   

 	float d = distance(uv, v);

    float c = smoothstep(d-smoothv, d+smoothv, 0.2);

	fragColor = vec4(c);
}
