#define BLU_SPEED	2.4
#define RED_SPEED	5.8


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 pos=(fragCoord.xy/iResolution.y);
	pos.x-=iResolution.x/iResolution.y/2.0;pos.y-=0.5;
	vec2 mse;
	//mse.x+=(mouse.x-0.5)*resolution.x/resolution.y;mse.y+=mouse.y-0.5;

	float fx=sin(pos.x*10.0+iTime*BLU_SPEED+mse.x*BLU_SPEED*2.0)/4.0;
	float dist=abs(pos.y-fx)*80.0;
	fragColor+=vec4(0.5/dist,0.5/dist,1.0/dist,1.0);

	fx=cos(pos.x*10.0+iTime*RED_SPEED+mse.x*RED_SPEED*2.0)/4.0;
	dist=abs(pos.y-fx)*80.0;
	fragColor+=vec4(1.0/dist,0.5/dist,0.5/dist,1.0);

	if (pos.y<=-0.2) {
		fragColor+=vec4(abs(pos.y+0.2),abs(pos.y+0.2),abs(pos.y+0.2),1.0);
        }
}
