void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 pixel = texture(iChannel0, uv);

    float blur = clamp(distance(pixel.rgb, vec3(0.0471,0.6353,0.1451))*2.,0.,1.);

    float brightness = dot(pixel, vec4(.05,.18,.02,.75));

    vec4 pixel2 = texture(iChannel1, uv+(texture(iChannel0, uv).rb-vec2(0.0471, 0.1451))*.1, blur*2.);

    fragColor = pixel2 * mix(1., brightness, blur);    
}
