float tri(float T, float A, float P) {
	return (A/P) * (P - abs(mod(T,(2.0*P)) - P) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) { 
    vec2 zoom = vec2(mix(1.0,10.0,(tri(iTime/1.0,1.0,6.0))));
    //vec2 zoom = vec2(10.0f);
	float mask = texture( iChannel0, (fragCoord.xy/iResolution.xy)*(vec2(1,1)/zoom)).a;
    vec4 clr = vec4(1.0,1.0,1.0,1.0);
    if (mask < 0.5) {
        clr *= 0.0;
    }
    float aar = mix(0.08, 0.007, (zoom.x-1.0)/9.0);
    clr *= mix(1.0/(aar*mask),0.0,(1.0-mask)/0.5);
    fragColor = clr;
}
