void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    float pepe = distance(vec4(iTime,iTime,iTime,iTime),iMouse)*0.8;
    float colx = sin(pepe+uv.x*50.0)+iTime*0.1;
	float coly = sin(pepe+uv.y*10.0)+iTime*0.3;
    float colz = cos(pepe+uv.x+uv.y*20.0)+iTime*0.2;
    // Output to screen
    fragColor = vec4(colx*0.5+uv.x*0.6,coly*0.3+uv.y*0.6,colz*0.40,1.0);
}
