void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 5.* u/R.y; 
                          // all these ingredients are needed. Effect is number-dependant
    O = vec4 ( U.y <   floor(  1e-6 * floor ( U.x*4e5 ) )  );    // bug    ( same for float(int()) )                                    
 // O = vec4 ( U.y <   floor(  4e-6 * floor ( U.x*1e5 ) )  );    // nobug                                                

    if ( mod(u.y, R.y/5.) < 1.) O = vec4(1,0,0,0);       // red integer grid 
}
