vec3 GetRainBow(float r)
{
	int i = int(3.*fract(r)); 
	vec4 c = vec4(.25);   
	c[(i+1)%3] += r = fract(3.*r);
	c[i] += 1.-r;
	return c.rgb;
}

vec3 bg(vec2 uv)
{
	float t = iTime;
	uv += iTime*0.01;
	uv *= 3.;
	float id = cos(uv.x) * cos(uv.y) + length(uv + t*0.2);
	return GetRainBow(id);
}

void mainImage( out vec4 fragColor, in vec2 g )
{
   	vec2 si = iResolution.xy;
    vec2 uv = (g+g - si)/si.y;
	float t = iTime * 0.2;
	uv.x += cos(t)*2.;
	uv.y += sin(t)*cos(t)*2.;
	float a = cos(uv.x) * sin(uv.y) * sin(t) * 0.5;
    uv *= mat2(cos(a),-sin(a),sin(a),cos(a)) * 3.;
	uv = floor(cos(uv)*30.) / 30.;
	fragColor = vec4(bg(uv * (length(uv) - 0.5)),1.);
}
